import { Injectable } from '@angular/core';
import { Book } from '../models/book.model';

@Injectable({
  providedIn: 'root',
})
export class BookService {
  private storageKey = 'books';

  private books: Book[] = [
    {
      title: 'Doctor Glas',
      authorName: 'Hjalmar SöderBerg',
      year: 1905,
      description: 'Doktor Glas är en dagboksroman av Hjalmar Söderberg som utkom 1905. Romanen skildrar en ensam läkares liv i Stockholm under några månader från 12 juni till 7 oktober i slutet av 1890-talet. Den medelålders medicine licentiaten Tyko Gabriel Glas längtar efter att få utföra en ”stor handling”.',
      coverImage: 'data:image/jpeg;base64,',
    },
    {
      title: 'Gösta Berlings Saga',
      authorName: 'Selma Lagerlöf',
      year: 1890,
      description: 'Gösta Berling beskrivs omväxlande som en försupen och avsatt präst vars liv är i spillror, omväxlande som en slags sagohjälte med nästan övernaturliga egenskaper. Särskilt mytologiseras hans förmåga att förföra traktens välbärgade unga kvinnor som Anna Stjärnhök, Marianne Sinclaire och grevinnan Elisabeth Dohna.',
      coverImage: 'data:image/jpeg;base64,',
    },
    {
      title: 'Bröderna Lejonhjärta',
      authorName: 'Astrid Lindgren',
      year: 1973,
      description: 'Astrid Lindgrens Bröderna Lejonhjärta är en saga om kampen mellan gott och ont. Tillsammans kämpar Jonatan och Skorpan för att det goda ska segra, och det blir en strid på liv och död mot förtryck och tyranneri för friheten och kärleken.',
      coverImage: 'data:image/jpeg;base64,',
    },
  ];

  getBooks(): Book[] {
    const booksJSON = localStorage.getItem(this.storageKey);
    if (booksJSON){
      return booksJSON ? JSON.parse(booksJSON) : [];
    }

    this.saveBooksToLocalStorage(this.books);
    return this.books;
  }

  addBook(book: Book): void {
    const books = this.getBooks();
    books.push(book);
    this.saveBooksToLocalStorage(books);
  }

  removeBook(index: number): void {
    const books = this.getBooks();
    if (index >= 0 && index < books.length) {
      books.splice(index, 1);
      this.saveBooksToLocalStorage(books);
    }
    else {
      console.error('Invalid index')
    }
  }

  updateBook(index: number, updatedBook: Book): void {
    const books = this.getBooks();
    if (index >= 0 && index < books.length) {
      books[index] = updatedBook;
      this.saveBooksToLocalStorage(books);
    } else {
      console.error('Invalid index for updating book');
    }
  }

  private saveBooksToLocalStorage(books: Book[]): void {
    if (books.length === 0) {
      console.warn("Book list is empty");
    }
    localStorage.setItem(this.storageKey, JSON.stringify(books));
  }
}
